(function() {
  'use strict';
  angular.module('ngAnimatedScrollTest', ['AnimatedScroll'])
    .controller('testController', ['$scope', 'animatedScroll',
    function($scope, animatedScroll) {
      $scope.scroll = function() {
        animatedScroll.scroll('#elementToScrollTo').then(function(element) {
          console.log('Finished scrolling to', element);
        });
      };
    }]);
})();
